<?phprequire '../includes/mySQL_Sessions.inc';require '../includes/TableRelated.inc';session_start();	if (empty($_SESSION['idnum']))	{ die("No User Specified"); }$sess_id = session_id();$key = dechex(mktime());$TbleGrp = GenerateTableGroup($_SESSION['idnum']);$clean['type'] = ereg_replace('\'', '`', $_GET['type']);$clean['refnum'] = ereg_replace('\'', '`', $_GET['refnum']);$clean['nxtaud'] = ereg_replace('\'', '`', $_GET['nxtaud']);$refnum = mysqlclean($clean,'refnum',8,$connection);$nxtaud = mysqlclean($clean,'nxtaud',8,$connection);if ($clean['type'] == 'artist'){	$clean['artist'] = ereg_replace('\'', '`', $_POST['artist']);	$artist = mysqlclean($clean,'artist',50,$connection);	$query['SaveArtist'] = "UPDATE {$TbleGrp['uploads']} SET artist='{$artist}' WHERE rank={$refnum}";	if (@mysql_query($query['SaveArtist'],$connection))	{	header("Location: ../aud/meta.php?type=artist&sess={$sess_id}&key={$key}&refnum={$refnum}&nxtaud={$nxtaud}&art=edt");		exit;}	}elseif ($clean['type'] == 'title'){	$clean['title'] = ereg_replace('\'', '`', $_POST['title']);	$title = mysqlclean($clean,'title',50,$connection);	$query['SaveTitle'] = "UPDATE {$TbleGrp['uploads']} SET title='{$title}' WHERE rank={$refnum}";	if (@mysql_query($query['SaveTitle'],$connection))	{	header("Location: ../aud/meta.php?type=title&sess={$sess_id}&key={$key}&refnum={$refnum}&nxtaud={$nxtaud}&ttl=edt");		exit;}	}elseif ($clean['type'] == 'cmnt'){	$clean['cmnt'] = ereg_replace('\'', '`', $_POST['cmnt']);	$cmnt = mysqlclean($clean,'cmnt',300,$connection);	$query['SaveCmnt'] = "UPDATE {$TbleGrp['uploads']} SET cmnt='{$cmnt}' WHERE rank={$refnum}";	if (@mysql_query($query['SaveCmnt'],$connection))	{	header("Location: ../aud/meta.php?type=cmnt&sess={$sess_id}&key={$key}&refnum={$refnum}&nxtaud={$nxtaud}");		exit;}	}?>