<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Frameset//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-frameset.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><?phprequire '../includes/mySQL_Sessions.inc';require '../includes/TableRelated.inc';require '../aud/aud_page_dim.inc';session_start();	if (empty($_SESSION['idnum']))	{ die("No User Specified"); }$idnum[0] = $_SESSION['idnum'];$TbleGrp = GenerateTableGroup($idnum[0]);$bg_color = $_SESSION['bg'];$ThemeColor = dechex($_SESSION['r']).dechex($_SESSION['g']).dechex($_SESSION['b']);$query['GetList'] = "SELECT * FROM {$TbleGrp['lists']} WHERE rank = {$_SESSION['curr_list']}";if ($ListItms = @mysql_fetch_array(@mysql_query($query['GetList'], $connection))){	for ($cntr = 1; $cntr <= 20; $cntr++)	{	$name = "itm" . $cntr;		if ($ListItms[$name] != 0)		{	$query['GetInfo'] = "SELECT file, title, artist, cmnt, ext FROM {$TbleGrp['uploads']} WHERE rank = {$ListItms[$name]}";			$SongInfo[$cntr] = @mysql_fetch_array(@mysql_query($query['GetInfo'], $connection));	}	}	}$ID = dechex($idnum[0]);$timestamp = dechex(mktime());$PlayList = "../tmp/lists/" . $ID . $timestamp . ".xspf";$Hndler = fopen($PlayList, 'w') or die("Can't Create Audio Pointer File");$FileData = '<?xml version="1.0" encoding="UTF-8"?>'	.		'<playlist version="1" xmlns="http://xspf.org/ns/0/">'	.		"<title>{$ListItms['title']}</title>"	.		'<trackList>'	;for ($rep = 1; $rep <= 20; $rep++){	if (!(empty($SongInfo[$rep]['file'])))	$FileData .= '<track>'		.		'<location>'		.'http://www.mysimum.com/tmp/aud/' . $_SESSION['tmpdir'] . $idnum[0].".".$SongInfo[$rep]['file'].".".$SongInfo[$rep]['ext']		.		'</location>'		.		"<title>{$SongInfo[$rep]['title']} ({$SongInfo[$rep]['artist']})</title>"//		.		'<annotation>' . $SongInfo[$rep]['cmnt'] . '</annotation>'		.		'</track>'		;}$FileData .= '</trackList></playlist>';fwrite($Hndler, $FileData);fclose($Hndler);$sh_SetPerm = "chmod a+rw " . $PlayList		."\n"."rm ../tmp/lists/" . $ID . $_SESSION['lastlist'] . ".xspf"		;exec($sh_SetPerm);$_SESSION['lastlist'] = $timestamp;$width = $Frame['player_w'] - 4;$height = $Frame['player_h'] - 4;$bgcolor = $Frame['bg'];?><head><?phpecho	'<style>'	.	'div.player {'		.	'position:absolute;'	.	'top:2px;'	.	'left:2px;'	.	'width:' . $width . 'px;'	.	'height:' . $height . 'px;'	.	'background-color:#' . $bgcolor . ';'		.	'}'	.	'</style>'	;?></head><body><div class="player"><?php$align = "middle";$name = "MP3 Player";echo	"\n".	'<object'			.	' align="' . $align . '"'			.	' width="' . $width . '"'			.	' height="' . $height . '"'			.	' id="' . $name . '"'			.	' classid="clsid:d27cdb6e-ae6d-11cf-96b8-444553540000"'			.	' codebase="http://fpdownload.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=7,0,0,0"'			.	'>'			.	"<param name=\"movie\" value=\"../aud_proc/audplayer_full.swf?playlist_url={$PlayList}\" />"			.	'<param ' . 'name="allowScriptAccess" ' . 'value="sameDomain" />'			.	'<param ' . 'name="bgcolor" ' . 'value="#' . $ThemeColor . '" />'			.	'<param ' . 'name="quality" ' . 'value="high" />'			.	'<embed'			.	" src=\"../aud_proc/audplayer_full.swf?playlist_url={$PlayList}\""			.	' align="' . $align . '"'			.	' name="' . $name . '"'			.	' width="' . $width . '"'			.	' height="' . $height . '"'			.	' bgcolor="#' . $ThemeColor . '"'			.	' type="application/x-shockwave-flash"'			.	' pluginspage="http://www.macromedia.com/go/getflashplayer"'			.	' quality="high"'			.	' allowScriptAccess="sameDomain"'			.	' />'			.	'</object>'			;?></div></body></html>