<?phprequire '../includes/mySQL_Sessions.inc';require '../includes/GetFileExtension.inc';require '../includes/DirectoryRelated.inc';require '../includes/TableRelated.inc';session_start();	if (empty($_SESSION['idnum']))	{ die("No User Specified"); }$idnum[0] = $_SESSION['idnum'];$sess_id = session_id();$key = mktime();$TbleGrp = GenerateTableGroup($idnum[0]);$SendString = "?sess={$sess_id}&key={$key}";$NumFiles = intval($_POST["NumFiles"]);for ($cnt=1;$cnt<=$NumFiles;$cnt++){		if (!(empty($_POST["AudTitle{$cnt}"])))		$clean['title'] = ereg_replace('\'', '`', $_POST["AudTitle{$cnt}"]);	else $clean['title'] = "Please Provide Title";	if (!(empty($_POST["AudArtist{$cnt}"])))		$clean['artist'] = ereg_replace('\'', '`', $_POST["AudArtist{$cnt}"]);	else $clean['artist'] = "Please Provide Artist";	if (!(empty($_POST["AudAlbum{$cnt}"])))		$clean['album'] = ereg_replace('\'', '`', $_POST["AudAlbum{$cnt}"]);	else $clean['album'] = "No Album";	$meta['title'] = mysqlclean($clean,'title',50,$connection);	$meta['artist'] = mysqlclean($clean,'artist',50,$connection);	$meta['album'] = mysqlclean($clean,'album',50,$connection);	if (!(empty($_FILES["AudFile{$cnt}"]['name'])))	{		$pext = getFileExtension($_FILES["AudFile{$cnt}"]['name']);		$pext = strtolower($pext);		if ($pext != "mp3")		{	unlink($_FILES["AudFile{$cnt}"]['tmp_name']);			$status[$cnt] = 'wrngext';		}		else		{	$UploadDir = "/cluster/harry/aud/mp3/" . GenerateFullImageDirectoryPath($idnum[0]);			$name = $key+$cnt;			$File['name'] = "{$idnum[0]}.{$name}.mp3";			$UploadFile = $UploadDir . $File['name'];						if (move_uploaded_file($_FILES["AudFile{$cnt}"]['tmp_name'], $UploadFile))			{	$FileName = mysqlclean($File, 'name', 25, $connection);								$query['UploadTable'] = "INSERT INTO {$TbleGrp['uploads']} VALUES "									.	"('','{$idnum[0]}','aud','{$name}',"									.	"'{$meta['title']}','{$meta['artist']}','No Comment'"									.	",0,0,'{$pext}')";				$query['AudioCount'] = "UPDATE filecount SET numaudio = numaudio + 1 WHERE idnum = '{$idnum[0]}'";				$query['GetRefNum'] = "SELECT rank FROM {$TbleGrp['uploads']} WHERE idnum = {$idnum[0]} AND file = '{$name}'";							if (@mysql_query ($query['UploadTable'], $connection))				{	if (@mysql_query ($query['AudioCount'], $connection))					{	if ($RefNum = @mysql_fetch_array(@mysql_query ($query['GetRefNum'], $connection)))						{	$status[$cnt] = 'uplsucc';				}	}	}			}			}	}}$_SESSION['status'] = $status[1];	header("Location: ../aud/upload.php{$SendString}");	exit;?>