<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><title>Delete User</title></head><body><p><?php$admin_pswd = "odysseus";require_once '../../includes/mySQL_Sessions.inc';require_once '../../includes/TableRelated.inc';require_once '../../includes/DirectoryRelated.inc';if (!($connection = @mysql_connect($sqlserver,$sqlusername,$sqluserpswd)))	die("Could not connect to database");if (!(mysql_select_db($databasename,$connection)))							showerror();$key = dechex(mktime());$clean['email'] = ereg_replace('\'', '`', $_POST['email']);$clean['pswd'] = ereg_replace('\'', '`', $_POST['pswd']);$clean['admin'] = ereg_replace('\'', '`', $_POST['admin']);$email = mysqlclean($clean,'email',50,$connection);$pswd = mysqlclean($clean,'pswd',20,$connection);$query['VerifyPswd'] = "SELECT idnum FROM login WHERE email='{$email}' AND pswd='{$pswd}'";if ($lgn = @mysql_fetch_array(@mysql_query($query['VerifyPswd'],$connection)))	$id = $lgn['idnum'];if	(	(empty($id))	|| ($clean['admin'] != $admin_pswd)	){	if (!(empty($clean['email'])))	echo "Invalid User Information Received. Please try again...";	else							echo "<br />";	echo "<br /><form method=\"post\" enctype=\"multipart/form-data\" action=\"delete_user.php?key={$key}\">"		."<br /><input type=\"text\" length=\"20\" name=\"email\" value=\"{$email}\" />email (for deletion)"		."<br /><input type=\"text\" length=\"20\" name=\"pswd\" value=\"{$pswd}\" />password (for deletion)"		."<br /><br /><input type=\"password\" length=\"20\" name=\"admin\" />administrator password"		."<br /><input type=\"submit\" value=\"Delete User\" />"		."</form>"		;}else{	$TbleGrp = GenerateTableGroup($id);	echo "<br />Deleting User: '{$email}'<br />\n";		echo "\n<br />login: ";	$query['login'] = "DELETE FROM login WHERE idnum={$id}";	if (@mysql_query($query['login'],$connection))		echo "deleted