count'],$connection))	echo "deleted";	else												echo "deletion failure";		echo "\n<br />msgs_arc: ";	$query['msgs_arc'] = "DELETE FROM {$TbleGrp['msgs_arc']} WHERE idnum={$id}";	if (@mysql_query($query['msgs_arc'],$connection))	echo "deleted";	else												echo "deletion failure";		echo "\n<br />msgs_rec: ";	$query['msgs_rec'] = "DELETE FROM {$TbleGrp['msgs_rec']} WHERE idnum={$id}";	if (@mysql_query($query['msgs_rec'],$connection))	echo "deleted";	else												echo "deletion failure";			echo "\n<br />msgs_snt: ";	$query['msgs_snt'] = "DELETE FROM {$TbleGrp['msgs_snt']} WHERE idnum={$id}";	if (@mysql_query($query['msgs_snt'],$connection))	echo "deleted";	else												echo "deletion failure";	echo "\n<br />uploads: ";	$query['uploads'] = "DELETE FROM {$TbleGrp['uploads']} WHERE idnum={$id}";	if (@mysql_query($query['uploads'],$connection))	echo "deleted";	else												echo "deletion failure";	echo "\n<br />albms: ";	$query['albms'] = "DELETE FROM {$TbleGrp['albms']} WHERE idnum={$id}";	if (@mysql_query($query['albms'],$connection))		echo "deleted";	else												echo "deletion failure";		echo "\n<br />grpcount: ";	$query['grpcount'] = "DELETE FROM grps.grpcount WHERE idnum={$id}";	if (@mysql_query($query['grpcount'],$connection))	echo "deleted";	else												echo "deletion failure";	echo "\n<br />profile: ";	$query['profile'] = "DELETE FROM profile WHERE idnum={$id}";	if (@mysql_query($query['profile'],$connection))	echo "deleted";	else												echo "deletion failure";	$DirPath = GenerateFullImageDirectoryPath($id);	$sh_DelAudDir = "rm -R /cluster/harry/aud/mp3/{$DirPath}";	$sh_DelImgDir = "rm -R /cluster/harry/img/photo/{$DirPath}";	exec($sh_DelAudDir);			echo "\n<br />Audio Directory: deletion attempted, please confirm.";	exec($sh_DelImgDir);			echo "\n<br />Image Directory: deletion attempted, please confirm.";		echo "\n<br /><input type=\"button\" value=\"Return\" onClick=\"top.location='delete_user.php?key={$key}';\" />";}?></p></body></html>