<?xml version="1.0" encoding="UTF-8"?><afAMPxml><?phprequire_once "../../includes/mySQL_Sessions.inc";require_once '../../includes/TableRelated.inc';if (!($connection=@mysql_connect($sqlserver,$sqlusername,$sqluserpswd)))		die();if (!(mysql_select_db($databasename,$connection)))								showerror();$clean = explode("_",str_replace('`',"'",$_GET['key']));$idnum = hexdec(mysqlclean($clean,0,8,$connection));$tmpdir = strval(mysqlclean($clean,1,16,$connection));$curr_list = intval(mysqlclean($clean,2,8,$connection));$startpt = intval($clean[3]);$TbleGrp = GenerateTableGroup($idnum);$query['list'] = "SELECT * FROM {$TbleGrp['lists']} WHERE rank={$curr_list}";if ($list = @mysql_fetch_array(@mysql_query($query['list'],$connection))){	$status = 0;	for ($i = $startpt; $i <= 50; $i++)	{	if ($list["itm{$i}"] != 0)		{	$query['info'] = "SELECT file,title,artist,album,bitrate,ext FROM {$TbleGrp['uploads']} WHERE rank=" . $list["itm{$i}"];			$info = @mysql_fetch_array(@mysql_query($query['info'],$connection));			echo "\n<sound>"			.	"<title> ".str_replace('`',"'",$info['title'])."</title>"			.	"<artist>".str_replace('`',"'",$info['artist']).", ".str_replace('`',"'",$info['album'])."</artist>"			.	"<bitrate>{$info['bitrate']}</bitrate>"			.	"<url>../tmp/aud/{$tmpdir}{$idnum}.{$info['file']}.{$info['ext']}</url>"			.	"</sound>";			$status = 1;		}	}	if ($status == 0)	{	echo	"\n<sound>"			.	"<url>http://www.djsattic.com/static/empty_playlist.mp3</url>"			.	"<title>This Playlist Is Empty</title>"			.	"<artist>This Playlist Is Empty</artist>"			.	"<bitrate>256</bitrate>"			.	"</sound>";	}}?></afAMPxml>