<?xml version="1.0" encoding="UTF-8"?><?phprequire_once "../../includes/mySQL_Sessions.inc";require_once '../../includes/TableRelated.inc';if (!($connection=@mysql_connect($sqlserver,$sqlusername,$sqluserpswd)))		die();if (!(mysql_select_db($databasename,$connection)))								showerror();$clean = explode("_",str_replace('`',"'",$_GET['key']));$idnum = hexdec(mysqlclean($clean,0,8,$connection));$tmpdir = strval($clean[1]);$rank = intval(mysqlclean($clean,2,8,$connection));$TbleGrp = GenerateTableGroup($idnum);$query['info'] = "SELECT title,artist,album,file,ext,bitrate FROM {$TbleGrp['uploads']} WHERE rank={$rank}";$info = @mysql_fetch_array(@mysql_query($query['info'],$connection));echo "<afAMPxml><sound>"	.	"<title> ".str_replace('`',"'",$info['title'])."</title>"	.	"<artist>".str_replace('`',"'",$info['artist']).", ".str_replace('`',"'",$info['album'])."</artist>"	.	"<bitrate>{$info['bitrate']}</bitrate>"	.	"<url>../tmp/aud/{$tmpdir}{$idnum}.{$info['file']}.{$info['ext']}</url>"	."</sound></afAMPxml>";?>