<?xml version="1.0" encoding="UTF-8"?><djsattic><?phprequire_once "../../includes/mySQL_Sessions.inc";require_once '../../includes/TableRelated.inc';require_once '../../includes/DirectoryRelated.inc';if (!($connection=@mysql_connect($sqlserver,$sqlusername,$sqluserpswd)))		die();if (!(mysql_select_db($databasename,$connection)))								showerror();$start = 1;$clean = explode("z",str_replace('`',"'",$_GET['key']));$idnum = hexdec(mysqlclean($clean,0,8,$connection));$curr_list = hexdec(mysqlclean($clean,1,8,$connection));$start = intval(mysqlclean($clean,2,2,$connection));$TbleGrp = GenerateTableGroup($idnum);$dir_path = "http://www.djsattic.com/tmp/remote/" . GenerateFullImageDirectoryPath($idnum);$query['list'] = "SELECT * FROM {$TbleGrp['lists']} WHERE rank={$curr_list}";if ($list = @mysql_fetch_array(@mysql_query($query['list'],$connection))){	$status = 0;	for ($i = $start; $i <= 50; $i++)	{	if ($list["itm{$i}"] != 0)		{	$query['info'] = "SELECT file,title,artist,bitrate,ext FROM {$TbleGrp['uploads']} WHERE rank=" . $list["itm{$i}"];			$info = @mysql_fetch_array(@mysql_query($query['info'],$connection));			echo "\n<sound>"			.	"<url>{$dir_path}{$idnum}.{$info['file']}.{$info['ext']}</url>"			.	"<title>{$info['title']}</title>"			.	"<artist>{$info['artist']}</artist>"			.	"<bitrate>{$info['bitrate']}</bitrate>"			.	"</sound>";			$status = 1;		}	}	if ($status == 0)	{	echo	"\n<sound>"			.	"<url>http://www.djsattic.com/static/empty_playlist.mp3</url>"			.	"<title>This Playlist Is Empty</title>"			.	"<artist>This Playlist Is Empty</artist>"			.	"<bitrate>256</bitrate>"			.	"</sound>";	}}?></djsattic>