<?xml version="1.0" encoding="UTF-8"?><djsattic><?phpheader('Content-type: text/xml');require_once '../../includes/mySQL_Sessions.inc';require_once '../../includes/TableRelated.inc';session_start();	if (empty($_SESSION['idnum']))	{ die(); }$TbleGrp = GenerateTableGroup($_SESSION['idnum']);$act = mysqlclean($_GET,'act',3,$connection);$ref = intval(mysqlclean($_GET,'ref',2,$connection));$above = "itm" . ($ref-1);$curr = "itm" . $ref;$below = "itm" . ($ref+1);$query['lst'] = "SELECT * FROM {$TbleGrp['lists']} WHERE rank={$_SESSION['curr_list']}";if ($itms = @mysql_fetch_array(@mysql_query($query['lst'],$connection))){		if (($act == 'mvu') && ($ref > 1) && ($ref <= 50))	{	$query['move'] =	"UPDATE {$TbleGrp['lists']}"							.	" SET {$above}={$itms[$curr]},{$curr}={$itms[$above]}"							.	" WHERE rank={$_SESSION['curr_list']}";		$dst = $ref-1;	}	elseif (($act == 'mvd') && ($ref < 50) && ($ref >= 1))	{	$query['move'] =	"UPDATE {$TbleGrp['lists']}"							.	" SET {$below}={$itms[$curr]},{$curr}={$itms[$below]}"							.	" WHERE rank={$_SESSION['curr_list']}";		$dst = $ref+1;						}		if (@mysql_query($query['move'],$connection))		echo "<src>{$ref}</src>"			."<dst>{$dst}</dst>"			;}?></djsattic>