<?xml version="1.0" encoding="UTF-8"?><djsattic><?phpheader('Content-type: text/xml');require_once '../../includes/mySQL_Sessions.inc';require_once '../../includes/TableRelated.inc';session_start();	if (empty($_SESSION['idnum']))	{ die(); }$key = mktime();$TbleGrp = GenerateTableGroup($_SESSION['idnum']);$new_ttl = "Untitled Playlist ".date("(g:i a)",$key);$query['UpdateCount'] = "UPDATE filecount SET audlists=audlists+1 WHERE idnum={$_SESSION['idnum']}";$query['CreateList'] =	"INSERT INTO {$TbleGrp['lists']} SET idnum={$_SESSION['idnum']}, type='aud'"						.	", title='{$new_ttl}', created={$key}";$query['GetLastID'] = "SELECT MAX(rank) AS last FROM {$TbleGrp['lists']} WHERE idnum={$_SESSION['idnum']}";if ( (@mysql_query($query['UpdateCount'],$connection)) && (@mysql_query($query['CreateList'],$connection)) ){	if ($id = @mysql_fetch_array(@mysql_query($query['GetLastID'], $connection)))	{	$_SESSION['curr_list'] = $id['last'];			echo	"<tt>{$new_ttl}</tt>"			.	"<id>{$id['last']}</id>";}	}?></djsattic>