<?xml version="1.0" encoding="UTF-8"?><djsattic><?phpheader('Content-type: text/xml');require_once '../../includes/mySQL_Sessions.inc';require_once '../../includes/TableRelated.inc';session_start();	if (empty($_SESSION['idnum']))	{ die(); }$TbleGrp = GenerateTableGroup($_SESSION['idnum']);$clean[0] = str_replace("'","`",$_GET['ttl']);$clean[1] = str_replace("\"","`",$clean[0]);$clean[2] = str_replace("&","and",$clean[1]);$newtitle = mysqlclean($clean,2,50,$connection);if (empty($newtitle))	$newtitle = "Untitled Playlist";$query['UpdTitle'] = "UPDATE {$TbleGrp['lists']} SET title='{$newtitle}' WHERE rank={$_SESSION['curr_list']}";if (@mysql_query($query['UpdTitle'],$connection)){	echo "<tt>". $newtitle ."</tt>"		."<id>{$_SESSION['curr_list']}</id>";}?></djsattic>