<?phprequire '../includes/mySQL_Sessions.inc';session_start();	if (empty($_SESSION['idnum']))	{ header("Location: http://www.mysimum.com/"); exit; }$idnum = $_SESSION['idnum'];$sess_id = session_id();$loc = $_GET['loc'];$th = $_GET['th'];$key = dechex(mktime());$dir = mysqlclean($_GET,'dir',2,$connection);$cl = mysqlclean($_GET,'cl',1,$connection);$it = mysqlclean($_GET,'it',1,$connection);if (($dir != 'up') && ($dir != 'dn')){	header("Location: ../user/items.php?sess={$sess_id}&loc={$loc}&th={$th}&key={$key}&edit=1&status=baddir");	exit;	}elseif ( ($cl < 0) || ($cl >= 4) || ($it < 0) || ($it > 7) ){	header("Location: ../user/items.php?sess={$sess_id}&loc={$loc}&th={$th}&key={$key}&edit=1&status=badloc");	exit;	}elseif ((($it == 0) && ($dir == 'up')) || (($it == 7) && ($dir == 'dn'))){	header("Location: ../user/items.php?sess={$sess_id}&loc={$loc}&th={$th}&key={$key}&edit=1&status=range");	exit;	}$src = "c{$cl}i{$it}";if ($dir == 'up')		$dst = "c{$cl}i" . ($it - 1);elseif ($dir == 'dn')	$dst = "c{$cl}i" . ($it + 1);$query['GetVals'] = "SELECT {$src}, {$dst} FROM items WHERE idnum={$idnum}";$GetVals = @mysql_fetch_array(@mysql_query ($query['GetVals'], $connection));$query['Update'] = "UPDATE items SET {$src}={$GetVals[$dst]}, {$dst}={$GetVals[$src]} WHERE idnum={$idnum}";if (@mysql_query ($query['Update'], $connection)){	header("Location: ../user/items.php?sess={$sess_id}&loc={$loc}&th={$th}&key={$key}&edit=1");	exit;	}?>