<?phprequire '../includes/mySQL_Sessions.inc';require '../includes/TableRelated.inc';if (!($connection = @mysql_connect($sqlserver,$sqlusername,$sqluserpswd)))	die("Could not connect to database");if (!(mysql_select_db($databasename,$connection)))							showerror();$key = mktime();$clean['id'] = hexdec(ereg_replace('\'', '`', $_GET['id']));$clean['fr'] = hexdec(ereg_replace('\'', '`', $_GET['fr']));$clean['do'] = ereg_replace('\'', '`', $_GET['do']);$id = mysqlclean($clean,'id',8,$connection);$fr = mysqlclean($clean,'fr',8,$connection);$TbleGrp['id'] = GenerateTableGroup($id);$TbleGrp['fr'] = GenerateTableGroup($fr);$query['appr_1'] = "UPDATE {$TbleGrp['id']['frnds']} SET status=1,created={$key},"					.	"cmnt='You have approved this friendship.' WHERE idnum={$id} AND frnd={$fr}";$query['appr_2'] = "UPDATE {$TbleGrp['fr']['frnds']} SET status=1,created={$key},"					.	"cmnt='Your friend request has been approved!' WHERE idnum={$fr} AND frnd={$id}";$query['cntr_1'] = "UPDATE frnds.frndcount SET frnds_req=frnds_req-1,frnds_appr=frnds_appr+1 WHERE idnum={$id}";$query['cntr_2'] = "UPDATE frnds.frndcount SET frnds_pend=frnds_pend-1,frnds_appr=frnds_appr+1 WHERE idnum={$fr}";//$query['SendMsg'] = "INSERT INTO {$TbleGrp['them']['msgs']} SET idnum={$clean['loc']}, sender={$_SESSION['idnum']}, status=2,"//				.	"created={$key, parent=0}";if ($clean['do'] == 'ap'){	if ((@mysql_query($query['appr_1'],$connection)) && (@mysql_query($query['appr_2'],$connection)))	{	if ((@mysql_query($query['cntr_1'],$connection)) && (@mysql_query($query['cntr_2'],$connection)))		{				die("friend approval was successful");}	}	}	//else//{	header("Location: ../user/infobox.php{$Rtrn}");		exit;//}?>