<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="content-type" content="text/html;charset=utf-8" /><title>mySimum.com: Search</title><?phprequire '../includes/mySQL_Sessions.inc';require '../includes/Console.inc';require '../includes/Styles-SideBar.inc';require '../includes/LoginBox.inc';if (!(empty($_GET['sess'])))	{	session_start();	if (empty($_SESSION['idnum']))	{ header("Location: http://www.mysimum.com/"); exit; }	$sess_id = session_id();	$ThemeColor = dechex($_SESSION['r']).dechex($_SESSION['g']).dechex($_SESSION['b']);}else{	$sess_id = "";	$ThemeColor = dechex(200).dechex(200).dechex(237);}$TextColor = ColorOffset($ThemeColor,(-92));echo BeginStyleDefinitions();echo SideBarStyles($bgcolor,$ThemeColor);if (empty($sess_id))	echo LoginBoxStyles();echo "a.users { text-decoration:none; }";echo EndStyleDefinitions();?></head><body><?phpecho SideBarSyntax($sess_id,$_SESSION['idnum'],'','srch','',$_SESSION['email'],$ThemeColor,$_SESSION['msgs']);if (empty($sess_id))	echo LoginBoxSyntax();$query['GetAllUsers'] = "SELECT idnum, firstname, lastname FROM names";$query['CountAllUsers'] = "SELECT COUNT(*) AS users FROM names";if ($Cnt = @mysql_fetch_array(@mysql_query($query['CountAllUsers'],$connection))){	if ($GetNames = @mysql_query($query['GetAllUsers'],$connection))	{	for ($rep = 1; $rep <= $Cnt['users']; $rep++)		{	$User[$rep] = @mysql_fetch_array($GetNames);			$PageLink[$rep] = "<a class=\"users\" href=\"../user/user.php?sess={$sess_id}&loc={$User[$rep]['idnum']}\">{$User[$rep]['firstname']} {$User[$rep]['lastname']}</a>";			$output .= "<br />" . $PageLink[$rep];		}	}}echo "<div style=\"font:18px arial;top:180px;left:300px;\">There are a total of {$Cnt['users']} registered users.</div>";echo "<div style=\"font:18px arial;top:200px;left:300px;\">" . $output . "</div>";?></body></html>