;echo " onLoad=\""	.	"top.options.location='../img/meta.php?sess={$sess_id}&type=opt&refnum=0';"	.	"\"";echo ">";$TopBarHt = 18;echo	"<div style=\"position:absolute;left:2px;top:0px;width:{$width}px;height:{$TopBarHt}px;"	.	"background-color:#{$ThemeColor};\">"	.	"<div class=\"info\">Album: {$Itm['title']}  --  {$clean['num']} photos</div>"	.	"</div>"	;$NumPerRow = 4;$row = 0;for ($i = 1; $i <= $clean['num']; $i++){	if ($chk == 4)	{	$row++;		$chk = 0;	}	$top = $TopBarHt+1+((floor(($i-1)/$NumPerRow))*($thmb_size+2));	$left = (2+(2+$thmb_size)*$chk);	$chk++;	if ($dim[$i][0] >= $dim[$i][1])		$dims = "width:".($thmb_size-2)."px;top:".(($thmb_size-$dim[$i][1])/2+2)."px;left:0px;";elseif ($dim[$i][0] < $dim[$i][1])	$dims = "height:".($thmb_size-2)."px;left:".(($thmb_size-$dim[$i][0])/2+2)."px;top:0px;";echo	"<div class=\"thmb\" style=\"top:{$top}px;left:{$left}px;\">"	.	"<img src=\"{$FilePath[$i]}\""	.	" onClick=\""	.		"top.mainphoto.location='../img/mainphoto.php?sess={$sess_id}&key={$key}&num={$clean['num']}&curr={$i}';"	.		"top.caption.location='../img/meta.php?sess={$sess_id}&type=cmnt&refnum={$Ref[$i]}';"	.		"top.options.location='../img/meta.php?sess={$sess_id}&type=opt&refnum={$Ref[$i]}';"	.		"\""	.	" style=\"{$dims}\""	.	">" 	.	"</div>";}?></body></html>