<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><?phprequire '../includes/mySQL_Sessions.inc';session_start();	if (empty($_SESSION['idnum']))	{ header("Location: http://www.mysimum.com/"); exit; }$idnum[0] = $_SESSION['idnum'];$idnum[1] = dechex($idnum[0]);$RefNum = $_GET['refnum'];$query['GetUserPageInfo'] = "SELECT numcol FROM pageinfo WHERE idnum = {$idnum[0]}";if ($PageInfo = @mysql_fetch_array(@mysql_query($query['GetUserPageInfo'],$connection)))	$NumberOfColumns = $PageInfo['numcol'];else	$NumberOfColumns = 3;$mysqlidnum = mysqlclean($idnum, 0, 8, $connection);$query['SearchItemTble'] =	"SELECT * FROM items "							.	"WHERE idnum = '{$mysqlidnum}'"							;if ($ItemArray = @mysql_fetch_array(@mysql_query ($query['SearchItemTble'], $connection))){//		Set default existence status		$ExistsAt = 'NOWHERE';			for($colrep = 0; $colrep < 4; $colrep++)				{	for($itmrep = 0; $itmrep < 8; $itmrep++)					{	$AttribName = 'c' . $colrep . 'i' . $itmrep;					if ($ItemArray[$AttribName] == $RefNum)	$ExistsAt = $AttribName;			}	}		if ($ExistsAt != 'NOWHERE')			$status = 'present';		elseif ($ExistsAt == 'NOWHERE'){		for($itmscan = 0; $itmscan < 8; $itmscan++)		{	for($colscan = 0; $colscan < $NumberOfColumns; $colscan++)					{	$AttribName = 'c' . $colscan . 'i' . $itmscan;					if ($ItemArray[$AttribName] == 0)						{	$OpenAt = $AttribName;								break;		}		}			if (!(empty($OpenAt)))		break;		}		if		(empty($OpenAt))		$status = "pagefull";		elseif	(!(empty($OpenAt)))	{			$query['UpdateItemTable'] =	"UPDATE items "							.	"SET {$OpenAt} = {$RefNum} "							.	"WHERE idnum = '{$mysqlidnum}'"	;			if (@mysql_query ($query['UpdateItemTable'], $connection))		$status = "added";		else 															$status = "addfail";	}}}elsedie ("Couldn't get necessary information");$_SESSION['status'] = $status;$HeaderInfo = "sess=" . session_id() . '&' . "key={$_GET['pageid']}" . '&' . "nxtimg=" . $_GET['nxtimg'];header("Location: ../user/img_gallery.php?{$HeaderInfo}");exit;?>