<?phprequire '../includes/mySQL_Sessions.inc';require '../includes/TableRelated.inc';session_start();	if (empty($_SESSION['idnum']))	{ die("No User Specified"); }$idnum[0] = $_SESSION['idnum'];$sess_id = session_id();$key = dechex(mktime());$TbleGrp = GenerateTableGroup($idnum[0]);$clean['slct'] = ereg_replace('\'', '`', $_GET['slct']);$clean['stat'] = ereg_replace('\'', '`', $_GET['stat']);$clean['bx'] = ereg_replace('\'', '`', $_GET['bx']);$slct = mysqlclean($clean,'slct',8,$connection);$query['GetMessage'] = "SELECT * FROM {$TbleGrp['msgs_rec']} WHERE rank={$slct}";$query['DeleteMessage'] = "DELETE FROM {$TbleGrp['msgs_rec']} WHERE rank={$slct}";$query['UpdateCount'] = "UPDATE msgs.msgcount SET msgs_rec_total=msgs_rec_total-1,msgs_archived=msgs_archived+1 WHERE idnum={$idnum[0]}";$query['UpdateUnreadCount'] = "UPDATE msgs.msgcount SET msgs_rec_unread=msgs_rec_unread-1 WHERE idnum={$idnum[0]}";if ($m = @mysql_fetch_array(@mysql_query($query['GetMessage'],$connection))){	$query['WriteMessage'] = "INSERT INTO {$TbleGrp['msgs_arc']} VALUES "					.	"('',{$m['idnum']},{$m['sender']},1,{$m['type']},'{$m['subject']}',{$m['refnum']},{$m['created']},{$m['parent']})";	if (@mysql_query($query['WriteMessage'],$connection))	{	if (@mysql_query($query['DeleteMessage'],$connection))		{	if (@mysql_query($query['UpdateCount'],$connection))			{	if ($clean['stat'] == 2)				{	@mysql_query($query['UpdateUnreadCount'],$connection);					$_SESSION['msgs'] = $_SESSION['msgs']-1;				}				header("Location: ../msg/msglist.php?sess={$sess_id}&bx={$clean['bx']}");				exit;}	}	}	}?>