<?phprequire '../includes/mySQL_Sessions.inc';require '../includes/TableRelated.inc';session_start();	if (empty($_SESSION['idnum']))	{ die("No User Specified"); }$idnum[0] = $_SESSION['idnum'];$sess_id = session_id();$key = dechex(mktime());$TbleGrp = GenerateTableGroup($idnum[0]);$clean['slct'] = ereg_replace('\'', '`', $_GET['slct']);$clean['stat'] = ereg_replace('\'', '`', $_GET['stat']);$clean['bx'] = ereg_replace('\'', '`', $_GET['bx']);$slct = mysqlclean($clean,'slct',8,$connection);if ($clean['bx'] == 'in'){	$query['DeleteMessage'] = "DELETE FROM {$TbleGrp['msgs_rec']} WHERE rank={$slct}";	$query['UpdateTotalCount'] = "UPDATE msgs.msgcount SET msgs_rec_total=msgs_rec_total-1 WHERE idnum={$idnum[0]}";	$query['UpdateUnreadCount'] = "UPDATE msgs.msgcount SET msgs_rec_unread=msgs_rec_unread-1 WHERE idnum={$idnum[0]}";	if (@mysql_query($query['DeleteMessage'],$connection))	{	if (@mysql_query($query['UpdateTotalCount'],$connection))		{	if ($clean['stat'] == 2)			{	@mysql_query($query['UpdateUnreadCount'],$connection);				$_SESSION['msgs'] = $_SESSION['msgs']-1;			}			header("Location: ../msg/msglist.php?sess={$sess_id}&bx={$clean['bx']}");			exit;}	}	}	elseif ($clean['bx'] == 'arc'){	$query['DeleteMessage'] = "DELETE FROM {$TbleGrp['msgs_arc']} WHERE rank={$slct}";	$query['UpdateCount'] = "UPDATE msgs.msgcount SET msgs_archived=msgs_archived-1 WHERE idnum={$idnum[0]}";	if (@mysql_query($query['DeleteMessage'],$connection))	{	if (@mysql_query($query['UpdateCount'],$connection))		{	header("Location: ../msg/msglist.php?sess={$sess_id}&bx={$clean['bx']}");			exit;}	}	}?>